import { useState } from "react";

function App() {
  const [hasDot, setHasDot] = useState(false);
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState(0);
  const [opr, setOpr] = useState("");

  const ops = ["/", "*", "+", "-"];

  const updateCalc = (value) => {
    if (value === ".") {
      console.log("hasDot", hasDot);
      if (!hasDot) {
        setHasDot(true);
      } else {
        return;
      }
    }
    if (ops.includes(value) && result === 0) {
      return;
    }
    setCalc(calc + value);
  };

  const add = () => {
    if (calc === "") {
      return;
    }
    setResult(result + Number(calc));
    setCalc("");
    setOpr("+");
    setHasDot(false);
  };

  const equal = () => {
    if (opr === "+") {
      setResult(result + Number(calc));
    }
    setResult(0);
  };

  const createDigit = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button onClick={() => updateCalc(i.toString())} key={i}>
          {i}
        </button>
      );
    }
    return digits;
  };

  return (
    <div className="App">
      <div className="calculator">
        <div className="display">
          <span></span> {calc || "0"}
        </div>

        <div className="extension">
          <button>CE</button>
          <button>C</button>
          <button>M</button>
          <button>MR</button>
          <button>DEL</button>
        </div>

        <div class="container">
          <div className="digits">
            {createDigit()}
            <button onClick={() => updateCalc("0")}>0</button>
            <button onClick={() => updateCalc(".")}>.</button>
            <button onClick={() => equal()}>=</button>
          </div>

          <div className="operators">
            <button onClick={add}>+</button>
            <button>-</button>
            <button>*</button>
            <button>/</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
