import { useState } from "react";

function App() {
  const [hasDot, setHasDot] = useState(false);
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState(0);
  const [opr, setOpr] = useState("");
  const [start, setStart] = useState(false);
  const [memory, setMemory] = useState(0);

  const ops = ["/", "*", "+", "-"];

  const updateCalc = (value) => {
    if (opr === "=") {
      setCalc(value);
      setOpr("");
      return;
    }
    if (value === ".") {
      console.log("hasDot", hasDot);
      if (!hasDot) {
        setHasDot(true);
      } else {
        return;
      }
    }
    if (ops.includes(value) && result === 0) {
      return;
    }
    setCalc(calc + value);
  };

  const add = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("+");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("+");
    setHasDot(false);
  };

  const divide = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("/");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("/");
    setHasDot(false);
  };

  const subtract = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("-");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("-");
    setHasDot(false);
  };

  const multiply = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("*");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("*");
    setHasDot(false);
  };

  const equal = () => {
    setCalc(eval(result + opr + calc).toString());
    setStart(false);
    setOpr("=");
  };

  const deleteLast = () => {
    if (calc === "") {
      return;
    }
    setCalc(calc.slice(0, -1));
  };

  const memoryAdd = () => {
    setMemory(parseFloat(calc));
  };

  const memoryRecall = () => {
    setCalc(memory.toString());
  };

  const createDigit = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button onClick={() => updateCalc(i.toString())} key={i}>
          {i}
        </button>
      );
    }
    return digits;
  };

  return (
    <div className="App">
      <div className="calculator">
        <div className="display">
          <span></span> {calc || result.toString()}
        </div>

        <div className="extension">
          <button>CE</button>
          <button>C</button>
          <button>M</button>
          <button>MR</button>
          <button onClick={deleteLast}>DEL</button>
        </div>

        <div class="container">
          <div className="digits">
            {createDigit()}
            <button onClick={() => updateCalc("0")}>0</button>
            <button onClick={() => updateCalc(".")}>.</button>
            <button onClick={equal}>=</button>
          </div>

          <div className="operators">
            <button onClick={add}>+</button>
            <button onClick={subtract}>-</button>
            <button onClick={multiply}>*</button>
            <button onClick={divide}>/</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
