import { useState } from "react";

function App() {
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");

  const ops = ["/", "*", "+", "-", "."];

  const updateCalc = (value) => {
    setCalc(calc + value);
  };

  const createDigit = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button onClick={() => updateCalc(i.toString())} key={i}>
          {i}
        </button>
      );
    }
    return digits;
  };
  return (
    <div className="App">
      <div className="calculator">
        <div className="display">
          <span></span> {calc || "0"}
        </div>

        <div className="extension">
          <button>CE</button>
          <button>C</button>
          <button>M</button>
          <button>MR</button>
          <button>DEL</button>
        </div>

        <div class="container">
          <div className="digits">
            {createDigit()}
            <button>0</button>
            <button>.</button>
            <button>=</button>
          </div>

          <div className="operators">
            <button>+</button>
            <button>-</button>
            <button>*</button>
            <button>/</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
