import { useState } from "react";

function App() {
  const [hasDot, setHasDot] = useState(false);
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState(0);
  const [opr, setOpr] = useState("");
  const [start, setStart] = useState(false);
  const [memory, setMemory] = useState(0);
  const [buttonClicks, setButtonClicks] = useState([]);

  const ops = ["/", "*", "+", "-"];

  const updateCalc = (value) => {
    if (opr === "=") {
      setCalc(value);
      setOpr("");
      return;
    }
    if (value === "0" && calc === "") {
      return;
    }
    if (value === ".") {
      console.log("hasDot", hasDot);
      if (!hasDot) {
        setHasDot(true);
      } else {
        return;
      }
    }
    if (ops.includes(value) && result === 0) {
      return;
    }
    setCalc(calc + value);
  };

  const add = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("+");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("+");
    setHasDot(false);
  };

  const divide = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("/");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("/");
    setHasDot(false);
  };

  const subtract = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("-");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("-");
    setHasDot(false);
  };

  const multiply = () => {
    if (calc === "") {
      return;
    }
    if (!start) {
      setResult(calc);
      setStart(true);
      setCalc("");
      setOpr("*");
      setHasDot(false);
      return;
    }
    setCalc(eval(result + opr + calc).toString());
    setResult(eval(result + opr + calc));
    setCalc("");
    setOpr("*");
    setHasDot(false);
  };

  const equal = () => {
    setCalc(eval(result + opr + calc).toString());
    setStart(false);
    setOpr("=");
  };

  const deleteLast = () => {
    if (calc === "") {
      return;
    }
    setCalc(calc.slice(0, -1));
  };

  const memoryAdd = () => {
    setMemory(calc.toString());
    setOpr("=");
  };

  const memoryRecall = () => {
    setCalc(memory.toString());
  };

  const clearEntry = () => {
    setCalc("");
  };

  const clear = () => {
    setCalc("");
    setResult(0);
    setOpr("");
    setStart(false);
    setHasDot(false);
  };

  const createDigit = () => {
    const digits = [];
    for (let i = 1; i < 10; i++) {
      digits.push(
        <button onClick={() => updateCalc(i.toString())} key={i}>
          {i}
        </button>
      );
    }
    return digits;
  };

  function handleClick(buttonText) {
    var timestamp = new Date().toISOString();
    const newButtonClicks = [...buttonClicks, buttonText];
    setButtonClicks(newButtonClicks);
    document.getElementById("result").value = buttonText;
  }

  function convertToCSV(data) {
    var csvContent = "data:text/csv;charset=utf-8,";
    data.forEach(function (rowArray) {
      var row = rowArray.join(",");
      csvContent += row + "\r\n";
    });
    return csvContent;
  }

  function downloadCSV() {
    var csvData = convertToCSV(buttonClicks);
    var encodedUri = encodeURI(csvData);
    document.getElementById("downloadLink").setAttribute("href", encodedUri);
  }

  return (
    <div className="App">
      <div className="calculator">
        <div className="display">
          <span></span> {calc || result.toString()}
        </div>

        <div className="extension">
          <button onClick={clearEntry}>CE</button>
          <button onClick={clear}>C</button>
          <button onClick={memoryAdd}>M</button>
          <button onClick={memoryRecall}>MR</button>
          <button onClick={deleteLast}>DEL</button>
        </div>

        <div class="container">
          <div className="digits">
            {createDigit()}
            <button onClick={() => updateCalc("0")}>0</button>
            <button onClick={() => updateCalc(".")}>.</button>
            <button onClick={equal}>=</button>
          </div>

          <div className="operators">
            <button onClick={add}>+</button>
            <button onClick={subtract}>-</button>
            <button onClick={multiply}>*</button>
            <button onClick={divide}>/</button>
          </div>
        </div>
      </div>
      <input type="text" id="result" disabled></input>
      <div className="history">
        <a href="#" id="downloadLink" download="calculator_data.csv">
          Download record.csv
        </a>
      </div>
    </div>
  );
}

export default App;
